mapper.select:

SELECT
	*
FROM HOST 
WHERE 
	email = #{email} 
AND pw = #{pw}

mapper.insert:
INSERT INTO HOST (
	no, email, pw, name, tel, 
	reg, biz_no, addr1, addr2, zip,
	imagename, imagesize, auth
)
VALUES (
	SEQ_HOST_NO.NEXTVAL, #{email}, #{pw}, #{name}, #{tel},
	sysdate, #{biz_no}, #{addr1}, #{addr2}, #{zip},
	#{imagename}, #{imagesize}, #{auth}
)
